import sys

from voe.pattern import node, wildcard


def action(vaip_pass, graph, softmax):
    # yapf: disable
    (graph.builder(vaip_pass)
     .set_op_type("hard_softmax","com.xilinx") # fix
     .clone_inputs(softmax.node())
     .clone_attrs(softmax.node())
     .add_string("type","poly")
     .clone_shape(softmax.node())
     .clone_data_type(softmax.node())
     .set_anchor_point1(softmax.node())
     .build()
     )
    return True


def pattern():
    sfm_in = wildcard()
    softmax = node("com.xilinx:softmax", sfm_in)
    return softmax.build(locals)


def process(vaip_pass, graph):
    # yapf: disable
    return (pattern(), action)
