class _SameAs(str):
    pass


class _SameAttrAs(object):
    def __init__(self, node_name, attr_name):
        self._node_name = node_name
        self._attr_name = attr_name

    def node_name(self):
        return self._node_name

    def attr_name(self):
        return self._attr_name


def same_as(x):
    return _SameAs(x)


def same_attr_as(node_name, attr_name):
    return _SameAttrAs(node_name, attr_name)


def isa_SameAs(obj):
    return isinstance(obj, _SameAs)


def isa_SameAttrAs(obj):
    return isinstance(obj, _SameAttrAs)
